) VALUES (
#{ id }, #{ userId }, #{ routerId }, #{ gridInfo }, #{ defaultCheck }, #{ remarks }, #{ createBy }, #{ createDate }, #{ updateBy }, #{ updateDate }, #{ delFlag },
)

mapper.update:
UPDATE user_grid SET
user_id = #{ userId }, router_id = #{ routerId }, grid_info = #{ gridInfo }, default_check = #{ defaultCheck }, remarks = #{ remarks }, update_by = #{ updateBy }, update_date = #{ updateDate },
WHERE id = #{id} and update_date = #{oldUpdateDate}

UPDATE user_grid SET
update_by = #{updateBy},
update_date = #{updateDate},
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
and update_date = #{oldUpdateDate}

mapper.insert:
