mapper.insert:
INSERT INTO sys_theme(
id, name, theme, is_default, remarks, create_by, create_date, update_by, update_date, del_flag,
) VALUES (
#{ id }, #{ name }, #{ theme }, #{ isDefault }, #{ remarks }, #{ createBy }, #{ createDate }, #{ updateBy }, #{ updateDate }, #{ delFlag },
)

mapper.update:
UPDATE sys_theme SET
name = #{ name }, theme = #{ theme }, is_default = #{ isDefault }, remarks = #{ remarks }, update_by = #{ updateBy }, update_date = #{ updateDate },
WHERE id = #{id} and update_date = #{oldUpdateDate}

UPDATE sys_theme SET
update_by = #{updateBy},
update_date = #{updateDate},
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
and update_date = #{oldUpdateDate}

mapper.insert:
INSERT INTO sys_theme(
id, name, theme, is_default, remarks, create_by, create_date, update_by, update_date, del_flag,
) VALUES
(
#{entity.id}, #{entity.name}, #{entity.theme}, #{entity.isDefault}, #{entity.remarks}, #{entity.createBy}, #{entity.createDate}, #{entity.updateBy}, #{entity.updateDate}, #{entity.delFlag},
)

mapper.update:
UPDATE sys_theme



      when id=#{entity.id} then #{entity.name}




      when id=#{entity.id} then #{entity.theme}




      when id=#{entity.id} then #{entity.isDefault}




      when id=#{entity.id} then #{entity.remarks}




      when id=#{entity.id} then #{entity.updateBy}




      when id=#{entity.id} then #{entity.updateDate}



WHERE

  id = #{entity.id} and update_date = #{entity.oldUpdateDate}

UPDATE sys_theme



      when id=#{entity.id} then 1




      when id=#{entity.id} then #{entity.updateBy}




      when id=#{entity.id} then #{entity.updateDate}



WHERE

  id = #{entity.id} and update_date = #{entity.oldUpdateDate}
